import { motion } from "framer-motion";

export default function HoverZoomCard() {
  return (
    <div className="relative w-80 h-56 overflow-hidden rounded-xl shadow-lg cursor-pointer group">
      {/* Background Image */}
      <motion.img
        src="https://images.unsplash.com/photo-1741851374467-c53876940807?q=80&w=2940&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D"
        alt="Camera"
        className="w-full object-cover"
        initial={{ height: "40%" }}
        whileHover={{ height: "100%", scale: 1.1 }}
        transition={{ type: "spring", stiffness: 150, damping: 12 }}
      />

      {/* Overlay */}
      <motion.div
        className="absolute inset-0 bg-black/50"
        initial={{ opacity: 0 }}
        whileHover={{ opacity: 1 }}
        transition={{ duration: 0.4 }}
      ></motion.div>

      {/* Heading */}
      <motion.div
        className="absolute top-4 left-4 text-white font-bold text-xl"
        initial={{ y: 0 }}
        whileHover={{ y: "90px", x: "50%", translateX: "-50%" }}
        transition={{ type: "spring", stiffness: 120, damping: 10 }}
      >
        TEXT HERE
      </motion.div>

      {/* Explore Button */}
      <motion.div
        className="absolute bottom-4 left-4 text-white flex items-center gap-1 opacity-0"
        initial={{ opacity: 0, y: 10 }}
        whileHover={{ opacity: 1, y: 0 }}
        transition={{ type: "spring", stiffness: 120, damping: 10, delay: 0.1 }}
      >
        <span>EXPLORE</span> <span className="text-lg">âžœ</span>
      </motion.div>
    </div>
  );
}
